    private void loginBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_loginBtnActionPerformed
        String user = userTxt.getText();
        String password = PasswordTxt.getText();
        
        int result = joinController.login(user, password);
        Dialog dialog = new Dialog();
        
        if (result  == 0) {
            joinController.setJoined(true);
        } else {
            
            if (result == 1) {
                // Usuario no exite
                dialog.setMessage("Usuario no encontrado");
            } else {
                // Contrase単a incorrecta
                dialog.setMessage("Contrase単a incorrecta");
            }
        }
    }//GEN-LAST:event_loginBtnActionPerformed

    private void signUpBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_signUpBtnActionPerformed
        String user = userTxt.getText();
        String password = PasswordTxt.getText();
        
        int result = joinController.signUp(user, password);
        Dialog dialog = new Dialog();
        
        if (result  == 0) {
            joinController.setJoined(true);
        } else if (result == 1) {
            // Este nombre de usuario ya eexiste       
            dialog.setMessage("Este usuario ya existe");
        } 
    }//GEN-LAST:event_signUpBtnActionPerformed


